function setCookie(o,e,n){const t=new Date;t.setTime(t.getTime()+24*n*60*60*1e3);const i="expires="+t.toUTCString();document.cookie=o+"="+e+";"+i+";path=/",console.log("쿠키 생성됨:",o,"=",e)}function getCookie(o){const e=o+"=",n=document.cookie.split(";");for(let o=0;o<n.length;o++){let t=n[o];for(;" "===t.charAt(0);)t=t.substring(1,t.length);if(0===t.indexOf(e))return t.substring(e.length,t.length)}return null}const modal=document.querySelector(".pop-up"),closeIcon=modal.querySelector(".close-icon");function closeModal(){modal.className="ir-hidden"}function hideForToday(){setCookie("hideModalToday","true",1),modal.className="ir-hidden"}closeIcon.addEventListener("click",function(){modal.className="ir-hidden"}),window.closeModal=closeModal,window.hideForToday=hideForToday;const hideToday=getCookie("hideModalToday");"true"===hideToday&&setTimeout(function(){modal.className="ir-hidden",console.log("쿠키가 있어서 모달을 숨겼습니다.")},10);