const menuWarp=document.querySelector(".menu-list"),mainContent=document.querySelector(".main-content"),menuList=menuWarp.querySelectorAll("li"),contents=mainContent.querySelectorAll("li"),menuLen=menuList.length,topIcon=document.querySelector("#top-icon"),mobileLi=document.querySelectorAll(".mobileMenuList > li");function goToScroll(e){window.scrollTo({top:e-100,behavior:"smooth"})}for(let e=0;e<menuLen;e++)menuList[e].addEventListener("click",()=>{goToScroll(contents[e].offsetTop)}),mobileLi[e].addEventListener("click",()=>{goToScroll(contents[e].offsetTop)});topIcon.addEventListener("click",()=>{window.scrollTo({top:0,behavior:"smooth"})});